  state = {
    key: 0,
  };

  componentDidMount() {
    window.addEventListener("keydown", (event) => {
      if (event.key === "3" && this.state.key === 0) this.setState({ key: 1 });
      else if (event.key === "0" && this.state.key === 1)
        this.setState({ key: 2 });
      else if (event.key === "0" && this.state.key === 2) {
        new Audio("https://static.wakmusic.xyz/static/audio/gosegu.mp3")
          .play()
          .then();
        this.setState({ key: 0 });
      } else this.setState({ key: 0 });
    });
  }

